import {clientUserList,saveClientUser,updateClientUser,changeUserDelStatus,changeUserVipStatus} from '../../utils/request';
export default {
    name: 'basetable',
    data() {
        return {
            query: {
                name: '',
                pageIndex: 1,
                pageSize: 10
            },
            tableData: [],
            multipleSelection: [],
            delList: [],
            addVisible: false,
            editVisible: false,
            pageTotal: 0,
            editForm: {},
            addForm: {},
            show:false,
            idx: -1,
            id: -1
        };
    },
    created() {
        this.clientUserList();
    },
    methods: {
        //停用启用状态变更
        changeDelStatus(event,row,index) {
            // 二次确认
            this.$confirm('确定要启用或停用此用户吗？', '提示', {
                type: 'warning'
            })
                .then(() => {
                    let param = new URLSearchParams();
                    param.append("id",row.id);
                    param.append("delStatus",row.delStatus);
                    this.listLoading = true;
                    //NProgress.start();
                    changeUserDelStatus(param).then((res) => {
                        let { message, status, datas } = res;
                        if (status !== 0) {
                            this.$message({
                            message: message,
                            type: 'error'
                            });
                        } else {
                            this.clientUserList();
                        }
                    });
                })
                .catch(() => {});
        },
        changeVipStatus(event,row,index){
            let param = new URLSearchParams();
            param.append("id",row.id);
            param.append("isVip",row.isVip);
            this.listLoading = true;
            //NProgress.start();
            changeUserVipStatus(param).then((res) => {
                let { message, status, datas } = res;
                if (status !== 0) {
                    this.$message({
                    message: message,
                    type: 'error'
                    });
                } else {
                    this.clientUserList();
                }
            });
        },
        //获取用户列表
        clientUserList() {
            let param = new URLSearchParams();
            param.append("paramStr",this.query.name);
            param.append("pageIndex",this.query.pageIndex);
            param.append("pageSize",10);
            this.listLoading = true;
            //NProgress.start();
            clientUserList(param).then((res) => {
                let { message, status, datas } = res;
                if (status !== 0) {
                    this.$message({
                    message: message,
                    type: 'error'
                    });
                } else {
                    this.pageTotal = datas.total;
                    this.tableData = datas.list;
                    this.listLoading = false;
                }
                //NProgress.done();
            });
        },
        // 触发搜索按钮
        handleSearch() {
            this.$set(this.query, 'pageIndex', 1);
            this.clientUserList();
        },
        // 多选操作
        handleSelectionChange(val) {
            this.multipleSelection = val;
        },
        //打开新增窗口
        handleAdd(){
            this.addVisible = true;
        },
        // 保存新增
        saveClientUser() {
            this.addVisible = false;
            //NProgress.start();
            saveClientUser(this.addForm).then((res) => {
                let { message, status, datas } = res;
                if (status !== 0) {
                    this.$message({
                    message: message,
                    type: 'error'
                    });
                } else {
                    this.clientUserList();
                }
                //NProgress.done();
            });
        },
        // 编辑操作
        handleEdit(index, row) {
            this.idx = index;
            this.editForm = row;
            this.editVisible = true;
        },
        // 保存编辑
        saveEdit() {
            this.editVisible = false;
            //NProgress.start();
            updateClientUser(this.editForm).then((res) => {
                let { message, status, datas } = res;
                if (status !== 0) {
                    this.$message({
                    message: message,
                    type: 'error'
                    });
                } else {
                    this.clientUserList();
                }
                //NProgress.done();
            });
        },
        // 分页导航
        handlePageChange(val) {
            this.$set(this.query, 'pageIndex', val);
            this.clientUserList();
        }
    }
};
